import React, { useState } from 'react'; // Ensure useState is imported from 'react'
import { useNavigate } from 'react-router-dom'; // Import useNavigate from 'react-router-dom'
import './LoginPage.css'; // Import the CSS for styling

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Create an instance of useNavigate

  const handleSubmit = (e) => {
    e.preventDefault();
    // Replace this with actual login logic
    console.log('Email:', email);
    console.log('Password:', password);
  };

  const handleRegisterClick = () => {
    navigate('/register'); // Navigate to the registration page
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Sign In</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="login-button">Sign In</button>
          <button type="button" className="login-button" onClick={handleRegisterClick}>Register</button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
