import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Importiere useNavigate
import './LoginPage.css'; // Importiere das CSS f체r die Gestaltung

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Erstelle eine Instanz von useNavigate

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ersetze dies durch die tats채chliche Anmeldelogik
    console.log('Email:', email);
    console.log('Password:', password);
  };

  const handleRegisterClick = () => {
    navigate('/register'); // Navigiere zur Registrierungsseite
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Sign In</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="login-button">Sign In</button>
          <button type="button"  className="login-button" > Register </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
